mapper.delete:
delete from tel_partner
where tel_id = #{id}
and partner_id = #{partnerId}

mapper.select:
SELECT id,name,contacts,tel from sys_company

    and name like concat('%',#{sysCompany.name},'%')

order by id in (
    SELECT partner_id from tel_partner WHERE tel_id = #{telId}
    ) desc

select count(1) from tel_partner
where partner_id = #{uid}
  and tel_id = #{telId}

SELECT t.id,u.username as userName,t.tel,z.name as surveyName,t.apply_time,t.status,t.final_cus_id,t.project_id as projectId FROM tel_protection t LEFT JOIN tel_partner p ON p.tel_id = t.id LEFT JOIN sys_user u ON t.final_cus_id = u.uid LEFT JOIN zs_project z ON t.project_id = z.id
p.partner_id = #{partnerId}
and username like concat('%',#{userName},'%')

SELECT z.id as projectId,u.username as userName,z.name as surveyName,z.created_at as createdAt
from zs_project z LEFT JOIN sys_user u ON z.company_id = u.uid
WHERE z.company_id = #{finalCusId}

mapper.update:
update
    tel_protection
set project_id = #{projectId} where id = #{id}
