mapper.delete:
delete from tel_partner
where tel_id = #{id}
and #{partnerId}

mapper.select:
select count(1) from tel_partner
where partner_id = #{uid}
  and tel_id = #{telId}

SELECT s.id,u.username,s.name as surveyName,s.created_at as createdAt,s.id as surveyId
from sys_user u LEFT JOIN zs_survey s ON s.final_cus_id = u.uid
WHERE u.uid  = #{uid}

mapper.update:
update
    tel_protection
set survey_id = #{surveyId} where id = #{id}
