mapper.delete:
delete from tel_partner
where tel_id = #{id}
and #{partnerId}

mapper.select:
select count(1) from tel_partner
where partner_id = #{uid}
  and tel_id = #{telId}

SELECT t.id,u.username,t.tel,t.apply_time,t.status,t.final_cus_id,t.project_id FROM tel_protection t LEFT JOIN tel_partner p ON p.tel_id = t.id LEFT JOIN sys_user u ON t.final_cus_id = u.uid LEFT JOIN zs_project z ON t.project_id = z.id
p.partner_id = #{partnerId}
and tel like concat('%',#{tel},'%')

SELECT z.id as project_id,u.username from zs_project z LEFT JOIN sys_user u ON z.company_id = u.uid
z.company_id = #{finalCusId}
and z.name like concat('%',#{projectName},'%')

select tel from tel_protection
where final_cus_id = #{userId}
and project_id = #{projectId}
and status = 1

mapper.update:
update
    tel_protection
set project_id = #{projectId}
where id = #{id}
