mapper.delete:
delete from tel_partner
where tel_id = #{id}
  and partner_id = #{partnerId}

mapper.select:
select 
from
tel_protection left join tel_partner on tel_protection.id = tel_partner.tel_id
left join sys_company on sys_company.id = tel_partner.partner_id

    and tel_protection.final_cus_id = #{finalCusId}
    and tel_protection.tel = #{tel}

select count(1) from tel_partner
where partner_id = #{uid}
  and tel_id = #{telId}

SELECT t.id,u.username,u.email,c.name,t.tel,t.apply_time,t.status,t.final_cus_id,tp.project_id,zs.name as projectName FROM tel_protection t LEFT JOIN tel_partner p ON p.tel_id = t.id LEFT JOIN sys_user u ON u.uid = t.final_cus_id LEFT JOIN sys_company c ON p.partner_id = c.id LEFT JOIN tel_project tp ON tp.tel_id = t.id LEFT JOIN zs_project zs ON tp.project_id = zs.id
p.partner_id = #{partnerId}
and t.tel like concat('%',#{tel},'%')

SELECT z.id,u.username,z.name,z.created_at from zs_project z LEFT JOIN sys_user u ON z.final_cus_id = u.uid
z.final_cus_id = #{finalCusId}
and z.name like concat('%',#{projectName},'%')

select count(1) from tel_project
where project_id = #{projectId}
  and tel_id = #{id}

mapper.delete:
delete from tel_project
where tel_id = #{id}
  and project_id = #{projectId}

mapper.update:
update tel_protection
set final_cus_id = #{finalCusId}
where tel = #{tel}
