mapper.select:
SELECT
    count(1) count,
    (count(1) / (
        SELECT
            count(1)
        FROM
            zs_answer_item
        WHERE
            question = #{qS}
        AND `option` = #{oS}
        AND creator IN(
            SELECT
                ai.creator
            FROM
                zs_answer_item ai
            LEFT JOIN zs_answer a ON a.id = ai.answer_id
            WHERE
                a.survey_id = #{surveyId}
            AND question = #{qF}
        )
    ) * 100) AS rate
FROM
    zs_answer_item
WHERE
    question = #{qS}
AND `option` = #{oS}
AND creator IN(
    SELECT
        ai.creator
    FROM
        zs_answer_item ai
    LEFT JOIN zs_answer a ON a.id = ai.answer_id
    WHERE
        a.survey_id = #{surveyId}
    AND question = #{qF}
    AND ai.`option` = #{oF}
)

select
from zs_answer_item
status = 1 and answer_id = #{id}

select
ai.id, ai.question, ai.value, ai.type, ai.status, ai.created_at, ai.creator, ai.`option`, ai.answer_id
 from zs_answer_item ai left join zs_answer a on a.id = ai.answer_id where a.survey_id = #{id}
