mapper.select:
select info.id cdr_id, answer.label, answer.in_valid_msg, answer.id, answer.survey_id, answer.agent_UUID, answer.status, answer.record, answer.seat, answer.created_at, answer.creator, answer.valid, answer.task_id, answer.review_spent, answer.work_num, answer.reviewer_name, answer.task_status,answer.start_time,answer.end_time,answer.call_lasts_time, answer.review_status, answer.review_msg, answer.reviewer,answer.uid,answer.checked, answer.reviewer_at, answer.username from zs_answer answer LEFT JOIN zs_cdrinfo info ON info.id = answer.agent_UUID
answer.status = 1
and answer.survey_id = #{surveyId}
and answer.valid = #{valid.code}
and answer.seat like concat('%', #{seat}, '%')
and answer.creator like concat('%', #{creator}, '%')
and answer.created_at >= #{startDate}
and answer.created_at <= #{endDate}
and answer.call_lasts_time is not null
and (answer.review_status is null or answer.review_status = 0)
and answer.review_status in (1,2)
and answer.review_status = #{reviewStatus}
and answer.reviewer_name like concat('%', #{reviewerName}, '%')
and answer.work_num like concat('%', #{workNum}, '%')

select
answer.agent_UUID
from
zs_answer answer
LEFT JOIN zs_cdrinfo info ON info.id = answer.agent_UUID

    info.record_file is not null

        and answer.survey_id = #{surveyId}


        and answer.valid = #{valid.code}


        and answer.seat like concat('%', #{seat}, '%')


        and answer.creator like concat('%', #{creator}, '%')


        and answer.created_at >= #{startDate}


        and answer.created_at <= #{endDate}

select result.option, result.num, result.count, concat((result.num/result.count) * 100, '%') rate
from (
  select ai.option, count(1) num ,
    (select count(1) from zs_answer_item ai left join zs_answer a on a.id = ai.answer_id where ai.question = #{question} and a.survey_id = #{id}) count
    from zs_answer_item ai left join zs_answer a on a.id = ai.answer_id where ai.question = #{question} and a.survey_id = #{id}
    GROUP BY ai.option
) result

select count(1) count from zs_answer_item ai left join zs_answer a on a.id = ai.answer_id where a.survey_id = 1 and  question = #{question} and `option` = #{option} and ai.creator in (
  select ai.creator from zs_answer_item ai left join zs_answer a on a.id = ai.answer_id where a.survey_id = #{surveyId} and ai.question = #{mainQuestion} and ai.`option` = #{mianOption}
)

select
from zs_answer
status = 1 and survey_id = #{surveyId}

select
from zs_answer
status = 1 and id = #{id}

select
from zs_answer
status = 1 and id = #{id}
